uint sub(UINT32 a, UINT32 b)
{
	if(a.high >= b.high)
	{
		if(a.high-b.high > 1)
		{
			return 0xFFFF;
		}
		if(a.high-b.high > 0)
		{
			if(a.low >= b.low)
			{
				return 0xFFFF;
			}
			else
			{
				return 0xFFFF - (b.low-a.low);
			}
		}
		if(a.high == b.high)
		{
			if(a.low > b.low)
			{
				return a.low - b.low;
			}
			else
			{
				return 0;
			}
		}
	}
	else
	{
		return 0;
	}
	return 0;
}

