void AD1KeyCheck()
{
	static uchar KeyStatus = KEYUP;
	static uchar key = 0;
	if(KeyStatus == KEYUP)
	{
		key = CheckKey(GetKeyValue(0));	// 
		if(key)
		{
			set_timer(2, 16);		// 
			KeyStatus = KEYDOWN;
		}
		else
		{
			KeyStatus = KEYUP;
		}
	}
	if(KeyStatus == KEYDOWN)
	{
		if(wait_timer(2))
		{
			if(key == CheckKey(GetKeyValue(0)))	// 
			{
				KeyStatus = KEYDONE;
				if(key == 2 || key == 3) // volume+/-
				{
					KeyStatus = KEYPRESS;
					set_timer(3, 200);
				}
			}
			else
			{
				KeyStatus = KEYUP;
			}
			if(KeyStatus == KEYDONE || KeyStatus == KEYPRESS)
			{
				SendArmData(key, 1);
			}
		}
	}
	if(KeyStatus == KEYDONE)
	{
		key = CheckKey(GetKeyValue(0));
		if(key == 0)
		{
			KeyStatus = KEYUP;
		}
	}
	if(KeyStatus == KEYPRESS)
	{
		if(key == CheckKey(GetKeyValue(0)))	// 
		{
			if(wait_timer(3))
			{
				SendArmData(key, 1);
				set_timer(3, 80);
			}
		}
		else
		{
			kill_timer(3);
			KeyStatus = KEYUP;
		}	
	}
}

